import React, { useEffect, useState } from 'react';
import copy from 'clipboard-copy';
import shareIcon from '../../images/shareIcon.svg';

export default function RecipesDone() {
  const doneRecipes = [
    {
      id: '52771',
      type: 'comida',
      area: 'Italian',
      category: 'Vegetarian',
      alcoholicOrNot: '',
      name: 'Spicy Arrabiata Penne',
      image: 'https://www.themealdb.com/images/media/meals/ustsqw1468250014.jpg',
      doneDate: '23/06/2020',
      tags: ['Pasta', 'Curry'],
    },
    {
      id: '178319',
      type: 'bebida',
      area: '',
      category: 'Cocktail',
      alcoholicOrNot: 'Alcoholic',
      name: 'Aquamarine',
      image: 'https://www.thecocktaildb.com/images/media/drink/zvsre31572902738.jpg',
      doneDate: '23/06/2020',
      tags: [],
    },
  ];

  localStorage.setItem('doneRecipes', JSON.stringify(doneRecipes));

  const [filterFoods, setFilterFood] = useState([]);
  console.log(filterFoods);

  function buttonsFilters() {
    if (type) {
      setFilterFood(doneRecipes.filter(({ type }) => type));
    }
  }

  function clipBoard(type, id) {
    copy(`http://localhost:3000/${type}s/${id}`);
  }

  return (
    <>
      <nav>
        <button type="button" data-testid="filter-by-all-btn" onClick={ () => buttonsFilters() }>All</button>
        <button type="button" data-testid="filter-by-food-btn">Food</button>
        <button type="button" data-testid="filter-by-drink-btn">Drinks</button>
      </nav>
      {doneRecipes
        .map(
          ({ id, image, category, name, doneDate, tags, type,
            area, alcoholicOrNot }, index) => (
            type === 'comida'
              ? (
                <div key={ id }>
                  <img
                    src={ image }
                    alt="imagem-receive"
                    data-testid={ `${index}-horizontal-image` }
                    width="150px"
                  />
                  <p data-testid={ `${index}-horizontal-name` }>{name}</p>
                  <p data-testid={ `${index}-horizontal-top-text` }>
                    {`${area} - ${category}`}
                  </p>
                  <p data-testid={ `${index}-horizontal-done-date` }>{doneDate}</p>
                  <p data-testid={ `${index}-${tags[0]}-horizontal-tag` }>
                    {tags[0]}
                  </p>
                  <p data-testid={ `${index}-${tags[1]}-horizontal-tag` }>
                    {tags[1]}
                  </p>
                  <button type="button" onClick={ () => clipBoard(type, id) }>
                    <img
                      data-testid={ `${index}-horizontal-share-btn` }
                      src={ shareIcon }
                      alt="share button"
                    />
                  </button>
                </div>)
              : (
                <div key={ id }>
                  <img
                    src={ image }
                    alt="imagem-receive"
                    data-testid={ `${index}-horizontal-image` }
                    width="150px"
                  />
                  <p data-testid={ `${index}-horizontal-name` }>{name}</p>
                  <p data-testid={ `${index}-horizontal-top-text` }>{alcoholicOrNot}</p>
                  <p data-testid={ `${index}-horizontal-done-date` }>{doneDate}</p>
                  <button type="button" onClick={ () => clipBoard(type, id) }>
                    <img
                      data-testid={ `${index}-horizontal-share-btn` }
                      src={ shareIcon }
                      alt="share button"
                    />
                  </button>
                </div>)
          ),
        )}
    </>
  );
}
