import React, { useState } from 'react';
import copy from 'clipboard-copy';
import { Link } from 'react-router-dom';
import shareIcon from '../../images/shareIcon.svg';
import blackHeartIcon from '../../images/blackHeartIcon.svg';

export default function RecipesFavorite() {
  const doneRecipes = [
    {
      id: '52771',
      type: 'comida',
      area: 'Italian',
      category: 'Vegetarian',
      alcoholicOrNot: '',
      name: 'Spicy Arrabiata Penne',
      image: 'https://www.themealdb.com/images/media/meals/ustsqw1468250014.jpg',
      doneDate: '23/06/2020',
      tags: ['Pasta', 'Curry'],
    },
    {
      id: '178319',
      type: 'bebida',
      area: '',
      category: 'Cocktail',
      alcoholicOrNot: 'Alcoholic',
      name: 'Aquamarine',
      image: 'https://www.thecocktaildb.com/images/media/drink/zvsre31572902738.jpg',
      doneDate: '23/06/2020',
      tags: [],
    },
  ];

  localStorage.setItem('doneRecipes', JSON.stringify(doneRecipes));

  const [filterRecipes, setFilterRecipes] = useState(doneRecipes);
  const [clipBoardFood, setClipBoardFood] = useState(false);
  const [clipBoardDrink, setClipBoardDrink] = useState(false);

  function buttonsFilters(param) {
    if (param === 'all') {
      setFilterRecipes(doneRecipes.filter(({ type }) => type));
    }
    if (param === 'food') {
      setFilterRecipes(doneRecipes.filter(({ type }) => type === 'comida'));
    } if (param === 'drinks') {
      setFilterRecipes(doneRecipes.filter(({ type }) => type === 'bebida'));
    }
  }

  function clipBoard(type, id) {
    copy(`http://localhost:3000/${type}s/${id}`);
    if (type === 'comida') {
      setClipBoardFood(true);
    } if (type === 'bebida') {
      setClipBoardDrink(true);
    }
  }

  function removeFavorite(target, idItem) {
    console.log(target);
    const obj = JSON.parse(localStorage.getItem('doneRecipes'));
    const newObj = obj.filter(({ id }) => id !== idItem);
    localStorage.setItem('doneRecipes', JSON.stringify(newObj));
  }

  return (
    <>
      <nav>
        <button
          type="button"
          data-testid="filter-by-all-btn"
          onClick={ () => buttonsFilters('all') }
        >
          All
        </button>
        <button
          type="button"
          data-testid="filter-by-food-btn"
          onClick={ () => buttonsFilters('food') }
        >
          Food
        </button>
        <button
          type="button"
          data-testid="filter-by-drink-btn"
          onClick={ () => buttonsFilters('drinks') }
        >
          Drinks
        </button>
      </nav>
      {filterRecipes
        .map(
          ({ id, image, category, name, doneDate, tags, type,
            area, alcoholicOrNot }, index) => (
            type === 'comida'
              ? (
                <div
                  key={ id }
                >
                  <Link to={ `/${type}s/${id}` }>
                    <img
                      width="150px"
                      src={ image }
                      alt="imagem-receive"
                      data-testid={ `${index}-horizontal-image` }
                    />
                    <p data-testid={ `${index}-horizontal-name` }>{name}</p>
                  </Link>
                  <p data-testid={ `${index}-horizontal-top-text` }>
                    {`${area} - ${category}`}
                  </p>
                  <p data-testid={ `${index}-horizontal-done-date` }>{doneDate}</p>
                  <p data-testid={ `${index}-${tags[0]}-horizontal-tag` }>
                    {tags[0]}
                  </p>
                  <p data-testid={ `${index}-${tags[1]}-horizontal-tag` }>
                    {tags[1]}
                  </p>
                  <button type="button" onClick={ () => clipBoard(type, id) }>
                    <img
                      data-testid={ `${index}-horizontal-share-btn` }
                      src={ shareIcon }
                      alt="share button"
                    />
                  </button>
                  <button type="button" onClick={ ({ target }) => removeFavorite(target, id) }>
                    <img
                      data-testid={ `${index}-horizontal-favorite-btn` }
                      src={ blackHeartIcon }
                      alt="share button"
                    />
                  </button>
                  <spam>{clipBoardFood === true ? 'Link copiado!' : null}</spam>
                </div>)
              : (
                <div key={ id }>
                  <Link to={ `/${type}s/${id}` }>
                    <img
                      width="150px"
                      src={ image }
                      alt="imagem-receive"
                      data-testid={ `${index}-horizontal-image` }
                    />
                    <p data-testid={ `${index}-horizontal-name` }>{name}</p>
                  </Link>
                  <p data-testid={ `${index}-horizontal-top-text` }>{alcoholicOrNot}</p>
                  <p data-testid={ `${index}-horizontal-done-date` }>{doneDate}</p>
                  <button type="button" onClick={ () => clipBoard(type, id) }>
                    <img
                      data-testid={ `${index}-horizontal-share-btn` }
                      src={ shareIcon }
                      alt="share button"
                    />
                  </button>
                  <button type="button" onClick={ ({ target }) => removeFavorite(target, id) }>
                    <img
                      data-testid={ `${index}-horizontal-favorite-btn` }
                      src={ blackHeartIcon }
                      alt="share button"
                    />
                  </button>
                  <spam>{clipBoardDrink === true ? 'Link copiado!' : null}</spam>
                </div>)
          ),
        )}
    </>
  );
}
