sourcesContent:
import React, { Component } from 'react';
import PropTypes from 'prop-types';
import '../styles/triviaCard.css';

class TriviaCard extends Component {
  constructor() {
    super()

    this.state = {
      seconds: 5,
      timer: '',
    }

    this.decrement = this.decrement.bind(this);
    this.startTimer = this.startTimer.bind(this)
    this.stopTimer = this.stopTimer.bind(this)
  }

  componentDidMount() {
    const { timer } = this.state;
    timer = this.startTimer()
  }

  componentDidUpdate() {
    const wrongAnswers = document.querySelectorAll('.wrong-answers');
    const correctAnswer = document.getElementById('correct-answer');
    wrongAnswers.forEach((button) => {
      button.classList.remove('incorrect');
    });
    correctAnswer.classList.remove('correct');
  }

  decrement() {
    this.setState((previousState) => ({
      seconds: previousState.seconds - 1,
    }))
  }

  startTimer() {
    setInterval(
      this.decrement, 1000)
  }

  stopTimer() {
    const { seconds, timer } = this.state;
    if(seconds === 0) {
      clearInterval(timer)
    }
  }

  verifyAnswers() {
    const wrongAnswers = document.querySelectorAll('.wrong-answers');
    const correctAnswer = document.getElementById('correct-answer');
    correctAnswer.classList.add('correct');
    wrongAnswers.forEach((button) => {
      button.classList.add('incorrect');
    });
  }

  render() {
    const {
      result: {
        category,
        question,
        correct_answer: correctAnswer,
        incorrect_answers: incorrectAnswers,
      },
    } = this.props;
    const { seconds } = this.state;

    return (
      <div>
        <h1 data-testid="question-category">{category}</h1>
        <h2 data-testid="question-text">{question}</h2>
        <button
          className="questions-buttons"
          type="button"
          id="correct-answer"
          data-testid="correct-answer"
          onClick={(e) => this.verifyAnswers(e)}
        >
          {correctAnswer}
        </button>
        {incorrectAnswers.map((answer, index) => (
          <button
            className="wrong-answers"
            type="button"
            data-testid={`wrong-answer-${index}`}
            key={answer}
            onClick={(e) => this.verifyAnswers(e)}
          >
            {answer}
          </button>
        ))}
        <p>{seconds}</p>
      </div>
    );
  }
}

TriviaCard.propTypes = {
  result: PropTypes.shape({
    category: PropTypes.string,
    question: PropTypes.string,
  }),
}.isRequired;
export default TriviaCard;
